mapper.insert:
        INSERT INTO apartment(
        	apart_id,
	        apart_name
		) VALUES (
	        #{apartment.apartId},
	        #{apartment.apartName}
		)

mapper.update:
UPDATE apartment SET 
	apart_id = #{apartment.apartId}, 
	apart_name = #{apartment.apartName}
	where apart_id = #{apartment.apartId}

mapper.insert:
        INSERT INTO floor(
        	apart_id,
	        floor_no
		) VALUES (
	        #{floor.apartId},
	        #{floor.floorNo}
		)

mapper.update:
UPDATE dormitory SET 
	fee = #{dorm.fee}, 
	leader_id = #{dorm.leaderId}
	where id = #{dorm.id}

mapper.insert:
        INSERT INTO bed(
        	bed_id,
	        dorm_id,
	        std_id
		) VALUES (
	        #{bed.bedId},
	        #{bed.dormId},
	        #{bed.stdId}
		)

mapper.update:
UPDATE bed SET 
	std_id = #{bed.stdId}
	where bed_id = #{bed.bedId} 
	and dorm_id = #{bed.dormId}
